package wyg.scw.common.bean;import lombok.Data;import java.util.HashMap;import java.util.Map;@Datapublic class AppResponse<T> {    private String code;    private String message;    private Map<String, Object> data = new HashMap<>();    private AppResponse(String code, String message) {        this.code = code;        this.message = message;    }    public static <T> AppResponse<T> ok(String message){        return new AppResponse<>("00000",message);    }    public static <T> AppResponse<T> ok(){        return new AppResponse<>("00000","success");    }    public static <T> AppResponse<T> fail(){        return new AppResponse<>("10001","fail");    }    public static <T> AppResponse<T> fail(String message){        return new AppResponse<>("10001",message);    }    public AppResponse<T> data(String key ,Object value){         this.data.put(key, value);         return this;    }}