package wyg.scw.common.utils;import com.alibaba.fastjson.JSON;import org.springframework.data.redis.core.StringRedisTemplate;import java.util.UUID;import java.util.regex.Matcher;import java.util.regex.Pattern;public class AppUtils {    /**     * 从redis中获取对象     * @param redisTemplate     * @param type     * @param token     * @param <T>     * @return     */    public static <T> T getObjectFromRedis(StringRedisTemplate redisTemplate,Class<T> type,String token ){        String jsonStr = redisTemplate.opsForValue().get(token);        return JSON.parseObject(jsonStr, type);    }    /**     * 将对象存入到redis中     * @param redisTemplate     * @param token     * @param t     * @param <T>     */    public static <T> void setObjectToRedis(StringRedisTemplate redisTemplate,String token,T t ){        String jsonString = JSON.toJSONString(t);        redisTemplate.opsForValue().set(token,jsonString);    }    /**     * 验证手机号码是否合法     * @param phoneNum     * @return     */    public static boolean isPhoneNum(String phoneNum) {        String regex = "^((13[0-9])|(14[5,7,9])|(15([0-3]|[5-9]))|(166)|(17[0,1,3,5,6,7,8])|(18[0-9])|(19[8|9]))\\d{8}$";        if (phoneNum.length() != 11) {            return false;        } else {            Pattern p = Pattern.compile(regex);            Matcher m = p.matcher(phoneNum);            boolean isMatch = m.matches();            return isMatch;        }    }    /**     * 生成uuid字符串     * @return     */    public static String getUUIDstr(){        return UUID.randomUUID().toString().replace("_","");    }}