package wyg.scw.scwuser.config;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;import wyg.scw.scwuser.utils.SmsTemplate;@Configurationpublic class ApplicationConfig {    @Bean    @ConfigurationProperties(prefix = "sms")    public SmsTemplate getSmsTemplate(){        return new SmsTemplate();    }    @Bean    public BCryptPasswordEncoder getBCryptPasswordEncoder(){        return new BCryptPasswordEncoder();    }}